CREATE TABLE "DDL_LOG" (	
  "ID" NUMBER(*,0) GENERATED BY DEFAULT AS IDENTITY INCREMENT BY 1 START WITH 1 NOCACHE  ORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
	"OPERATION" VARCHAR2(30 CHAR), 
	"OBJ_TYPE" VARCHAR2(255 CHAR), 
	"OBJ_OWNER" VARCHAR2(30 CHAR), 
	"OBJECT_NAME" VARCHAR2(255 CHAR), 
	"SQL_TEXT" CLOB, 
	"TBL_DDL_BEFORE_DROP" CLOB, 
	"ATTEMPT_BY" VARCHAR2(30 CHAR), 
	"ATTEMPT_DT" DATE, 
	"SESSION_USER" VARCHAR2(255 CHAR), 
	"SESSION_HOST" VARCHAR2(255 CHAR), 
	"IP_ADDRESS" VARCHAR2(255 CHAR), 
	"MODULE" VARCHAR2(255 CHAR), 
	"OS_USER" VARCHAR2(255 CHAR), 
	"SERVER_HOST" VARCHAR2(255 CHAR), 
	"SERVICE_NAME" VARCHAR2(255 CHAR), 
	"SESSION_USERID" VARCHAR2(255 CHAR), 
	"SESSIONID" VARCHAR2(255 CHAR), 
	"SID" VARCHAR2(255 CHAR), 
	"TERMINAL" VARCHAR2(255 CHAR), 
	"STATEMENTID" VARCHAR2(255 CHAR)
);
